// set system mode, so decoupled functions can respond
context.global.mode = "initialising";

return msg;

var dataArray = msg.payload.split(' ');
context.global.mode = "receiving";
dataArray.pop(); // last element is newline

var pHMSB = dataArray.pop();
var pHLSB = dataArray.pop(); 
var pH = 256* parseFloat(pHMSB)+ parseFloat(pHLSB);

var pHMsg  = {payload: pH}; 

return [pHMsg];

/* 
	Raw light reading needs scaling to lux
*/

var light = msg.payload;
if (light<50)
  {
  lightLevel="Very Dark!";
  }
else if (light<200)
  {
  lightLevel="Dark";
  }
  else if (light<240)
  {
  lightLevel="Light";
  }
else
  {
  lightLevel="Very Light!";
  }

return {payload: lightLevel};

/* 
	Raw humidity reading is twice real reading
*/

var humid = msg.payload;
humid = humid / 2;

return {payload: humid};

/* 
	Raw temperature reading is ten times too big
*/

var temp = msg.payload;
temp = temp / 10;


return {payload: temp};

if (msg.payload == '1') {
	msg.payload = 'Battery Low!';
} else {
	msg.payload = 'Battery Normal';
}
return msg;

// clear alerts
context.global.tempalarm = 0;
context.global.lightalarm = 0;
return msg;

/* 
	Raise alarm when necessary
*/
if (msg.payload > 24 && msg.topic == "Air Temperature" && context.global.tempalarm != 1)
	{
	context.global.tempalarm = 1;
	msg.payload = "I'm too hot in here! I'm melting!";
	return msg;
	}
if (msg.payload > 1000 && msg.topic == "Light Level" && context.global.lightalarm != 1)
	{
	context.global.lightalarm = 1;
	msg.payload = "It's too light in here! I'm fading fast!";
	return msg;
	}
return null;

// set system mode, so decoupled functions can respond
context.global.mode = "shuttingdown";

return msg;

var dataArray = msg.payload.split(' ');
context.global.mode = "receiving";
dataArray.pop(); // last element is newline

var tempMSB = dataArray.pop();
var tempLSB = dataArray.pop(); 
var humidity = dataArray.pop();
var light = dataArray.pop();
var temp = 256* parseFloat(tempMSB)+ parseFloat(tempLSB);

var lightMsg  = {payload: light}; 
var humidityMsg = {payload: humidity}; 
var tempMsg = {payload: temp};

return [lightMsg, humidityMsg, tempMsg];

/* 
	Raw pH reading is 100x real reading
*/

var pH = msg.payload;
pH = pH / 100;

return {payload: pH};

var dataArray = msg.payload.split(' ');
context.global.mode = "receiving";
dataArray.pop(); // last element is newline

dataArray.pop();
dataArray.pop();
var luxMSB = dataArray.pop();
var luxLSB = dataArray.pop(); 
var lux = 256* parseFloat(luxMSB)+ parseFloat(luxLSB);

var luxMsg  = {payload: lux}; 

return [luxMsg];
