var dataArray = msg.payload.toString().split(' ');

dataArray.pop(); // last element is newline

var pHMSB = dataArray.pop();
var pHLSB = dataArray.pop(); 
var pH = 256* parseFloat(pHMSB)+ parseFloat(pHLSB);

var pHMsg  = {payload: pH}; 

return [pHMsg];

/* 
	Raise alarm when necessary
*/
if (msg.payload > 26) {
	msg.payload = "Temp";
	return msg;
}

/* 
	Raw pH reading is 100x real reading
*/

var pH = msg.payload;
pH = pH / 100;

return {payload: pH};

/* 
	Raise alarm when necessary
*/
if (msg.payload > 1000) {
	msg.payload = "Light";
	return msg;
}

var msg, textMsg;
context.textEnabled = context.textEnabled || 'Off'

// turn text messaging on or off
if (msg.topic == 'Text Messaging') {
	context.textEnabled = msg.payload;
	msg.payload = 'Text Messaging ' + msg.payload;
}

// raise an alert
if (msg.topic == 'Alert') {
	msg.payload = 'Help! hey man its getting too hot' + msg.payload + '!';
	if (context.textEnabled == 'on') {
		textMsg = {payload: 'Texting texting ' + (msg.payload)};
	    msg.payload += ' (Sending text)';
	}
}

return [msg, textMsg];

var msgString = msg.payload.toString(); 
if (msgString.indexOf("failed") != -1) {
	errorMsg = {payload: msgString};
	return [null, errorMsg];
}

// Fetch array from buffer
var rawArray = msgString.split(/\s/);

var errorMsg = null; // will populate on error
msg = null; // will populate if no errors

// strip off preamble and final newline
var dataArray = rawArray.slice(11, -2);

var nodeId = dataArray[0];
var numBytesExpected = dataArray[1];
var numBytesGot = dataArray.length - 2;
var sensorId = dataArray[2];
var readings = dataArray.slice(4);

// check integrity
if (nodeId > 63 || nodeId < 32) {
	errorMsg = {payload: 'Invalid Node Id: ' + nodeId}; 
} else if (sensorId > 15) {
	errorMsg = {payload: 'Invalid Sensor Id: ' + sensorId};
} else if (numBytesExpected != numBytesGot) {
	errorMsg = {payload: 'Expected ' + numBytesExpected + ' bytes, got ' + numBytesGot};
} else {
	msg = {
		nodeId: nodeId,
		sensorId: sensorId,
		readings: readings
	}
}

return [msg, errorMsg];

// Array contains two readings, each occupying 2
// elements (bytes).

var MSB = msg.readings.pop();
var LSB = msg.readings.pop(); 
var secondReading = 256* parseFloat(MSB)+ parseFloat(LSB);

var MSB = msg.readings.pop();
var LSB = msg.readings.pop(); 
var firstReading = 256* parseFloat(MSB)+ parseFloat(LSB);

msg.firstReading = firstReading
msg.secondReading = secondReading;

return msg;

/* 
	Raw temperature reading is ten times too big
*/

var temp = msg.secondReading;
temp = temp / 10;


return {payload: temp};

/* 
    1023 steps, 4.5 max voltage
*/

var rawReading = msg.firstReading;
var voltsFloat = rawReading / 1023 * 5.16; 
var volts = voltsFloat.toFixed(2);
return {payload: volts};
